CREATE TABLE solana_data.smart_money_tx (`tx_hash` String, `trade_type` String, `pool_address` String, `block_height` UInt64, `transaction_time` UInt64, `wallet_address` String, `token_amount` Float64, `token_symbol` String, `token_address` String, `quote_symbol` String, `quote_amount` Float64, `quote_address` String, `quote_price` Decimal(18, 8), `usd_price` Decimal(18, 8), `usd_amount` Decimal(18, 8)) ENGINE = MergeTree PARTITION BY toYYYYMM(toDateTime(transaction_time)) ORDER BY (token_address, transaction_time, wallet_address, tx_hash) SETTINGS index_granularity = 8192